ready = function() {
	$('a[href^="#"]').on('click',function (e) {
		e.preventDefault();
	
		var target = this.hash;
		var $target = $(target);
	
		$('html, body').stop().animate({
			'scrollTop': $target.offset().top
		}, 1200, 'swing', function () {
			window.location.hash = target;
		});
	});

	$(window).scroll(function(){
		if($(this).scrollTop() > 1){
			$(".bar").stop(true,false).animate({opacity:0.9},800);
		}
		else
		{
			$(".bar").stop(true,false).animate({opacity:0},400);
		}
	});
};

$(document).ready(ready); // DON'T REMOVE THIS
$(document).on('page:load', ready); // DON'T REMOVE THIS
